const loadImageFullScreen = (imageUrl) => {
    // Create a new image element
    const img = new Image();
  
    // Set the source of the image to the provided URL
    img.src = imageUrl;
  
    // Create a new div element to contain the image
    const div = document.createElement('div');
    div.style.position = 'fixed';
    div.style.top = 0;
    div.style.left = 0;
    div.style.width = '100%';
    div.style.height = '100%';
    div.style.display = 'flex';
    div.style.justifyContent = 'center';
    div.style.alignItems = 'center';
    div.style.backgroundColor = 'black';
    div.style.zIndex = 9999;
  
    // Add the image to the div
    div.appendChild(img);
  
    // Append the div to the body of the document
    document.body.appendChild(div);
  };
  
  // Call the function with the URL of the image you want to load
  loadImageFullScreen('https://i.postimg.cc/PJ3G0N1q/ezgif-com-rotate.gif');
