import React from "react";
import { Box, Typography, useTheme, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { NavLink } from "react-router-dom";
import axios from "axios";
import toast from "react-hot-toast";

const Navbar = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  const loggedIn = JSON.parse(localStorage.getItem("authToken"));

  //handle logout
  const handleLogout = async () => {
    try {
      await axios.post("/api/v1/auth/logout");
      localStorage.removeItem("authToken");
      toast.success("Logout successful");
      navigate("/login");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        backgroundColor: theme.palette.background.alt,
        backgroundImage: "url('https://i.postimg.cc/cLPSbmgy/bg2.gif')",
        backgroundSize: "cover",
        p: "1rem 6%",
        mb: 2,
        boxShadow: "0 3px 5px rgba(0, 0, 0, 0.3)",
      }}
    >
       <Typography variant="h1" color="LightBlue" fontWeight="bold" style={{ fontFamily: "'Julius Sans One', sans-serif" }}>
        Heathy Cravings
      </Typography>

      <Typography
        variant="h2"
        color="SalmonPink"
        sx={{
          fontFamily: "'Amatic SC', cursive",
          fontSize: "1.5rem",
          lineHeight: "1.2",
          mt: "1rem",
          textAlign: "center",
        }}
      >
        Join us on your journey to better health today!
      </Typography>

      <Box sx={{ display: "flex", alignItems: "center" }}>
        <NavLink
          to="/"
          sx={{ textDecoration: "none", color: theme.palette.text.primary, mr: 1 }}
        >
          <Button variant="contained" color="secondary">
            Home
          </Button>
        </NavLink>
        {loggedIn ? (
          <>
            <Button
              onClick={handleLogout}
              variant="contained"
              color="secondary"
              size="small"
              sx={{ ml: 5 }}
            >
              Logout
            </Button>
          </>
        ) : (
          <>
            <NavLink
              to="/register"
              sx={{
                textDecoration: "none",
                color: theme.palette.text.primary,
              }}
              p={1}
            >
              <Button
                variant="contained"
                color="secondary"
                size="small"
                sx={{ ml: 2 }}
              >
                Sign Up
              </Button>
            </NavLink>
            <NavLink
              to="/login"
              sx={{
                textDecoration: "none",
                color: theme.palette.text.primary,
              }}
              p={1}
            >
              <Button variant="contained" color="secondary" size="small">
                Sign In
              </Button>
            </NavLink>
          </>
        )}
      </Box>
    </Box>
  );
};


export default Navbar;
